  return (
    <>
      <>
        <h1>문제1</h1>
        <ul>
          {/* list */}
          {/* 예시 데이터입니다 */}
          <li>
            <h3>{playlist[1].title}</h3>
            <p>Joe Hisaishi</p>
          </li>
        </ul>
        <div>
          <p>
            곡명 : <input />
          </p>
          <p>
            가수/작곡 : <input />
          </p>
          <p>
            <button>추가</button>
          </p>
        </div>
      </>
    </>
  );
}
export default State1;
