import { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import productList from "../__mock__/products.json";
import Table from "react-bootstrap/Table";

function DetailPage() {
  const params = useParams();
  console.log(params.productNumber);
  const [product, setProduct] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    setProduct(productList.products);
  }, [params.productNumber]);

  const productFind = () => {
    const findProduct = product.find(
      (p) => p.productNumber === params.productNumber
    );

    if (findProduct) {
      return (
        <div>
          <Table striped bordered hover variant="dark">
            <thead>
              <tr>
                <th>#</th>
                <th>First Name</th>
                <th>Last Name</th>
                <th>Username</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>1</td>
                <td>Mark</td>
                <td>Otto</td>
                <td>@mdo</td>
              </tr>
              <tr>
                <td>2</td>
                <td>Jacob</td>
                <td>Thornton</td>
                <td>@fat</td>
              </tr>
              <tr>
                <td>3</td>
                <td colSpan={2}>Larry the Bird</td>
                <td>@twitter</td>
              </tr>
            </tbody>
          </Table>
          <h4>상품명: {findProduct.productName}</h4>
          <div>가격: {findProduct.productPrice}원</div>
          <div>상품번호: {findProduct.productNumber}</div>
          <div>상품사이즈: {findProduct.productSize}</div>
          <div>상품평점: {findProduct.productRating}</div>
          <div>상품평 개수: {findProduct.productReview}</div>
          <br />
          <div>
            리뷰:
            {findProduct.Review.map((a, i) => {
              return (
                <div key={i}>
                  <div>작성자: {a.reviewer} </div>
                  <div>리뷰: {a.review}</div>
                  <div>평점: {a.rating}</div>
                </div>
              );
            })}
          </div>
        </div>
      );
    } else {
      navigate("/state");
    }
  };

  return (
    <div>
      {productFind()}
      {/* 
      상세 페이지는 자유롭게 꾸미시면 됩니다.
      아직 해당 부분의 진도가 나가지 않았기 때문에 주소의 파람을 가지고 올 수 있는 방법은
      미리 콘솔에 찍어두었습니다.

      단, 없는 번호 상품으로 접근 시 state페이지로 돌아가도록 구현해주세요
    */}
    </div>
  );
}

export default DetailPage;
