function Comment({ post }) {
  return (
    <div>
      {post.Comments.map((a, i) => {
        <S.CommentItem key={i}>
          <p>
            작성자: <span>{a.User.nickname}</span>
          </p>
          <p>
            댓글 내용: <span>{a.content}</span>
          </p>
        </S.CommentItem>;
      })}
    </div>
  );
}

export default Comment;

const CommentItem = styled.li`
  border: 1px solid #000;
  margin: 10px;
`;

const S = {
  CommentItem,
};
