import { useState } from "react";
import styled from "styled-components";

function Comment({ post, setPost }) {
  const [btnState, setBtnState] = useState(true);
  const [handleInput, setHandleInput] = useState("");
  const handelBtn = (e) => {
    if (e.target.name === "btn1") {
      console.log(btnState);
      setBtnState(false);
    }
    if (e.target.name === "btn2") {
      console.log(btnState);
      setBtnState(false);
    }
  };
  const handelBtn2 = () => {
    setBtnState(true);
  };

  const onChangeInput = (e) => {
    setHandleInput(e.target.value);
    console.log(handleInput);
  };

  const handleComment2 = () => {
    const updatePost2 = {
      ...post,
      Comments: [
        ...post.Comments,
        {
          User: {
            nickname: post.Comments[i].User.nickname,
          },
          content: handleInput,
          myComment: true,
        },
      ],
    };
    setPost(updatePost2);
  };

  return (
    <div>
      {post.Comments.map(function (a, i) {
        return (
          <S.CommentItem key={i}>
            <p>
              작성자: <span>{a.User.nickname}</span>
            </p>
            <p>
              댓글 내용: <span>{a.content}</span>
            </p>

            {a.myComment ? (
              btnState ? (
                <button onClick={handelBtn} name="btn1">
                  수정
                </button>
              ) : null
            ) : null}
            {a.myComment ? (
              btnState ? (
                <button onClick={handelBtn} name="btn2">
                  삭제
                </button>
              ) : null
            ) : null}
            {a.myComment ? (
              !btnState ? (
                <div>
                  <input onChange={onChangeInput} />
                  <button onClick={handelBtn2}>완료</button>
                </div>
              ) : null
            ) : null}
          </S.CommentItem>
        );
      })}
    </div>
  );
}

export default Comment;

const CommentItem = styled.li`
  border: 1px solid #000;
  margin: 10px;
`;

const S = {
  CommentItem,
};
