import styled from "styled-components";

function Comment({ post, buttonB }) {
  return (
    <div>
      {post.Comments.map(function (a, i) {
        return (
          <S.CommentItem key={i}>
            <p>
              작성자: <span>{a.User.nickname}</span>
            </p>
            <p>
              댓글 내용: <span>{a.content}</span>
            </p>
            {a.myComment && buttonB ? <button>수정</button> : null}
            {a.myComment && buttonB ? <button>삭제</button> : null}
          </S.CommentItem>
        );
      })}
    </div>
  );
}

export default Comment;

const CommentItem = styled.li`
  border: 1px solid #000;
  margin: 10px;
`;

const S = {
  CommentItem,
};
