import styled from "styled-components";

const handelBtn = (e) => {
  if (e.target.name === "btn1") {
    console.log("hi");
  }
  if (e.target.name === "btn2") {
    console.log("nono");
  }
};

function Comment({ post }) {
  return (
    <div>
      {post.Comments.map(function (a, i) {
        return (
          <S.CommentItem key={i}>
            <p>
              작성자: <span>{a.User.nickname}</span>
            </p>
            <p>
              댓글 내용: <span>{a.content}</span>
            </p>
            {a.myComment ? <button name="btn1">수정</button> : null}
            {a.myComment ? <button name="btn2">삭제</button> : null}
          </S.CommentItem>
        );
      })}
    </div>
  );
}

export default Comment;

const CommentItem = styled.li`
  border: 1px solid #000;
  margin: 10px;
`;

const S = {
  CommentItem,
};
