import styled from "styled-components";

function Comment({ post }) {
  return (
    <div>
      {post.Comments.map(function (a, i) {
        return (
          <S.CommentItem key={i}>
            <p>
              작성자: <span>{a.User.nickname}</span>
            </p>
            <p>
              댓글 내용: <span>예시 내용</span>
            </p>
          </S.CommentItem>
        );
      })}
    </div>
  );
}
