import axios from "axios";
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

function DetailPage() {
  const params = useParams();
  console.log(params.productNumber);
  const [product, setProduct] = useState(null);

  useEffect(() => {
    // 외부 JSON 데이터 가져오기
    axios
      .get("외부 JSON 데이터 URL")
      .then((response) => {
        // JSON 데이터에서 해당 상품 번호를 찾아서 상태에 설정
        const foundProduct = response.data.find(
          (item) => item.productNumber === params.productNumber
        );
        setProduct(foundProduct);
      })
      .catch((error) => {
        console.error("실패", error);
      });
  }, [params.productNumber]);

  return (
    <div>
      {/* 
      상세 페이지는 자유롭게 꾸미시면 됩니다.
      아직 해당 부분의 진도가 나가지 않았기 때문에 주소의 파람을 가지고 올 수 있는 방법은
      미리 콘솔에 찍어두었습니다.

      단, 없는 번호 상품으로 접근 시 state페이지로 돌아가도록 구현해주세요
    */}

      {params.productNumber}
    </div>
  );
}

export default DetailPage;
