import styled from "styled-components";

function Comment({ post }) {
  const commentItems = post.Comments.map((a, i) => {
    // console.log(a, i);
    // console.log(a.User.nickname);
    const userNick = a.User.nickname;
    console.log(userNick);
    return (
      <S.CommentItem key={i}>
        <p>
          작성자: <span>{a.User}</span>
        </p>
        <p>
          댓글 내용: <span>예시 내용</span>
        </p>
      </S.CommentItem>
    );
  });
}
export default Comment;

const CommentItem = styled.li`
  border: 1px solid #000;
  margin: 10px;
`;

const S = {
  CommentItem,
};
