import { useEffect, useState } from "react";

const Timer = () => {
  const a = 0;
  useEffect(() => {
    const timer = setInterval(() => {
      console.log("타이머 작동");
      a += 1;
    }, 2000);
    return () => {
      clearInterval(timer);
    };
  }, []);
  return <div>{a}</div>;
};

export default Timer;
