import { useState } from "react";

const Timer = (props) => {
  const [num, setNum] = useState(0);
  useEffect(() => {
    const timer = setInterval(() => {
      setNum(num + 1);
      console.log("타이머 작동");
    }, 2000);
    return () => {
      clearInterval(timer);
    };
  }, []);
  return <div>{num}</div>;
};

export default Timer;
