import { useRef, useState } from "react";
import styled from "styled-components";
import Child01 from "./child";

function Q2() {
  // const arr = [];
  // const [forceRender, setForceRender] = useState(false);
  // // const use = useRef();

  // const onAddList = () => {
  //   setForceRender((prev) => !prev);
  //   arr.push();
  // };
  const [state, setState] = useState(0);
  const use = useRef();
  const arr = [];
  const showCurrent = () => {
    arr.push(use.current.value);
  };

  const onChange = () => {
    setState(state + 1);
    console.log(arr);
  };

  // const listItem = arr.map((num, idx) => <li key={idx}>{num}</li>);

  const arr2 = [1, 2, 3];
  // const listItems2 = arr2.map((num, idx) => <li num={num}></li>);
  return (
    <>
      <h1>문제2</h1>
      <div>
        <h2>문제 2-1</h2>
        <p>
          <input ref={use} />
        </p>
        <p>
          <button onClick={showCurrent}>추가</button>
        </p>
        <p>
          <button onClick={onChange}>제출</button>
        </p>
        <p></p>
        <ul></ul>
        <ul>
          {arr.map((bt) => (
            <Child01 name={bt} />
          ))}
        </ul>
      </div>
      <div>
        <h2>문제 2-2</h2>
        <p> 이 문구는 아래 버튼을 누르면 색상이 바뀝니다</p>
        <button>변경</button>
      </div>
    </>
  );
}
export default Q2;

const Main = styled.li`
  color: red;
`;
